import React, {useState, useContext} from 'react';

const ScoreContext = React.createContext();
export const useScore = () =>{
    return useContext(ScoreContext)
}

const GameContext = ({children}) =>{
    const [state, setState] = useState({
        playerOne: 0,
        playerTwo: 0,
        nameOne: '',
        nameTwo: '',
        line: '',
        activeModal: false

    });

    const increment = (activeWord) =>{
        if(activeWord === 'X'){
            setState({...state, playerOne: state.playerOne + 1})
        }
        if(activeWord === 'O'){
            setState({...state, playerTwo: state.playerTwo + 1})
        }
    };
    const activeModal = () =>{
        setState({...state, activeModal: !state.activeModal});
    }
    const players = (data) =>{
        setState({
            ...state, nameOne: state.nameOne = data.nameOnePlayer, nameTwo: state.nameTwo = data.nameTwoPlayer
        })
    }
    const showLine = (data) =>{
        const result = data.reduce((cart, item, index, arr)=>{
            return cart += item
        }, 0)
        switch (result) {
            case 3 : setState({...state, line: state.line = 'line activeXOne'})

        }
    }



    return(
        <ScoreContext.Provider value={{
            visible: state,
            increment,
            playerName: state.name,
            activeModal,
            players,
            showLine
        }} >
            {children}
        </ScoreContext.Provider>
    )

}
export default GameContext;
