import React, { useState, useRef} from 'react';
import './field.scss'
import Square from "../Square/Square";
import {logicWin} from './logicWin';
import {v4 as uuidv4} from 'uuid'
import {useScore} from "../../Context/Context";
import Modal from "../Modal/Modal";


const Field = () =>{
    const context = useScore();
    const [state, setState] =useState({
        arrSquares: Array(9).fill(null),
        count: 0,
        activeModal: false
    });

    const ref = useRef();
    const nodeList = ref.current && ref.current.children
    if(nodeList.length){
        nodeList[5].classList.add('active')
    }

    const winnerPlayer = () =>{
        const activeSymbol = state.count % 2 === 0 ? 'X' : 'O';
        const result = logicWin.forEach((item, index) =>{
            const line = logicWin[index];
            if(state.arrSquares[line[0]] === activeSymbol && state.arrSquares[line[1]] === activeSymbol && state.arrSquares[line[2]] === activeSymbol){

console.log(nodeList[line[0]])
                    nodeList[line[0]].classList.add('active')
                    nodeList[line[1]].classList.add('active')
                    nodeList[line[2]].classList.add('active')


                context.increment(activeSymbol)

                // setTimeout(()=>{
                //     setState({...state, arrSquares: Array(9).fill(null), count: 0})
                // }, 500);
            }
            if(state.count === 8 || state.count === 7){
                setState({
                    ...state, activeModal: true
                })
                setTimeout(()=>{
                    setState({...state, arrSquares: Array(9).fill(null), count: 0, activeModal: false})
                }, 1000)
            }
        })
    };
    const handelClick = (event) =>{
        let data = event.target.getAttribute('data');
        let current = state.arrSquares;
        if(current[data] === null){
            current[data] = state.count % 2 === 0 ? 'X' : 'O';
            setState({
                ...state,
                arrSquares: current
            })
            setState({
                ...state,
                count: state.count + 1
            })
        }
        winnerPlayer()
    }
    const showSquare = state.arrSquares.map((item, index) =>
        <Square  key={uuidv4()} content={item} onClick={handelClick} data={index} />)


    return(
        <div className='field-container'>
<div className='field-container' ref={ref}>
    {showSquare}
</div>


            {state.activeModal ? <Modal content={'Н И Ч Ь Я'} active={true} /> : null}
        </div>
    )

}

export default Field;
