import React, {useEffect} from 'react';
import './game.scss';
import Field from "../Field/Field";
import {useScore} from "../../Context/Context";
import Modal from "../Modal/Modal";

const Game = () =>{
    const scorePlayer = useScore();

    useEffect(() =>{
        setTimeout(() =>{
            scorePlayer.visible.activeModal();

        }, 1000)

    }, [])

    return(
        <div className='wrapper'>
            <Modal activeClass='active' />
            <Field  />
            <div className='score-container'>
                <h3 className='content'>Score</h3>
                <p className='content'>1 {scorePlayer.visible.nameOne}: {scorePlayer.visible.playerOne}</p>
                <p className='content'>2 {scorePlayer.visible.nameTwo}: {scorePlayer.visible.playerTwo} </p>
            </div>
        </div>
    )

}

export default Game;
