import React from 'react';
import './field.scss'
import Square from "../Square/Square";
import {v4 as uuidv4} from 'uuid'

const Field = () =>{
    const han = (event) =>{
        let data = event.target.getAttribute('data');
        console.log(data)
    }

    const showSquare = Array(9).fill(null).map((item, index) =>{
        return(
            <Square key={uuidv4()} content={item} onClick={han} data={index} />
        )
    })


    return(
        <div className='field-container'>
            {showSquare}
        </div>
    )

}

export default Field;
