import React, {useState} from 'react';
import './field.scss'
import Square from "../Square/Square";
import {logicWin} from './logicWin';
import {v4 as uuidv4} from 'uuid'
import {useScore} from "../../Context/Context";
import Modal from "../Modal/Modal";

const Field = () =>{
    const context = useScore();
    const [state, setState] =useState({
        arrSquares: Array(9).fill(null),
        count: 0,
        activeModal: false
    });


    const winnerPlayer = () =>{
        const activeWord = state.count % 2 === 0 ? 'X' : 'O';
        const result = logicWin.forEach((item, index) =>{
            const line = logicWin[index];
            if(state.arrSquares[line[0]] === activeWord && state.arrSquares[line[1]] === activeWord && state.arrSquares[line[2]] === activeWord){
                context.showLine(line)
                context.increment(activeWord)

                setTimeout(()=>{
                    setState({...state, arrSquares: Array(9).fill(null), count: 0})
                }, 1000);
            }
            if(state.count === 8){
                context.activeModal()
                setTimeout(()=>{
                    setState({...state, arrSquares: Array(9).fill(null), count: 0, activeModal: true})
                }, 1000)
            }
        })
    };

    const handelClick = (event) =>{
        let data = event.target.getAttribute('data');
        let current = state.arrSquares;
        if(current[data] === null){
            current[data] = state.count % 2 === 0 ? 'X' : 'O';
            setState({
                ...state,
                arrSquares: current
            })
            setState({
                ...state,
                count: state.count + 1
            })
        }
        winnerPlayer()

    }

    const showSquare = state.arrSquares.map((item, index) =>
        <Square key={uuidv4()} content={item} onClick={handelClick} data={index} />)

    return(
        <div className='field-container'>
            {showSquare}
            <div className=''></div>
            <Modal active={state.activeModal} setActive={context.activeModal} />
        </div>
    )

}

export default Field;
