import {v4 as uuidv4} from 'uuid'
import {useScore} from "../../Context/Context";
import Modal from "../Modal/Modal";


const Field = () =>{
    const context = useScore();
    const [state, setState] =useState({
        arrSquares: Array(9).fill({
            value: null,
            active: false
        }),
        count: 0,
        activeModal: false,
    });



