import React, {useState} from 'react';
import {useScore} from "../../Context/Context";

const Form = () =>{
    const data = useScore();
    const [state, setState] = useState({
        nameOnePlayer: '',
        nameTwoPlayer: ''
    });

    const changeOnePlayer = (event) =>{
        setState({
            ...state, nameOnePlayer: event.target.value
        })
    }
    const changeTwoPlayer = (event) =>{
        setState({
            ...state, nameTwoPlayer: event.target.value
        })
    }
    const handelChange = (event) =>{
        event.target.preventDefault();
        console.log('this')
        data.players(state)

    }


    return(
        <form onSubmit={handelChange} >
            <input onChange={changeOnePlayer} placeholder='Name player one ...' type="text"/>
            <input onChange={changeTwoPlayer} placeholder='Name player two ...' type="text"/>
            <input type="button"/>
        </form>
    )

}

export default Form;
