import React, { useState, useRef} from 'react';
import './field.scss'
import Square from "../Square/Square";
import {logicWin} from './logicWin';
import {v4 as uuidv4} from 'uuid'
import {useScore} from "../../Context/Context";
import Modal from "../Modal/Modal";


const Field = () =>{
    const context = useScore();
    const [state, setState] =useState({
        arrSquares: Array(9).fill({
            value: null,
            active: false
        }),
        count: 0,
        activeModal: false,
    });

    const winnerPlayer = () =>{
        const activeSymbol = state.count % 2 === 0 ? 'X' : 'O';
        const result = logicWin.forEach((item, index) =>{
            const line = logicWin[index];
            if(state.arrSquares[line[0]].value === activeSymbol && state.arrSquares[line[1]].value === activeSymbol && state.arrSquares[line[2]].value === activeSymbol){

                context.increment(activeSymbol)

                // setTimeout(()=>{
                //     setState({...state, arrSquares: Array(9).fill(null), count: 0})
                // }, 500);
            }
            if(state.count === 8 || state.count === 7){
                setState({
                    ...state, activeModal: true
                })
                setTimeout(()=>{
                    setState({...state, arrSquares: Array(9).fill(null), count: 0, activeModal: false})
                }, 1000)
            }
        })
    };
    const handelClick = (event) =>{
        let data = event.target.getAttribute('data');
        let current = JSON.parse(JSON.stringify(state.arrSquares))
        console.log(state)

        if(current[data].value === null){
            current[data].value = state.count % 2 === 0 ? 'X' : 'O';
            console.log(current)
            setState({
                ...state,
                arrSquares: current,
                count: state.count + 1
            })
            // setState({
            //     ...state,
            //     count: state.count + 1
            // })
        }
        // winnerPlayer()


    }
    const showSquare = state.arrSquares.map((item, index) =>
        <Square key={uuidv4()} content={item.value} onClick={handelClick} data={index} />)

    return(
        <div className='field-container'>
            {showSquare}
            {state.activeModal ? <Modal content={'Н И Ч Ь Я'} active={true} /> : null}
        </div>
    )

}

export default Field;
