import React, {useState} from 'react';
import './field.scss'
import Square from "../Square/Square";
import {logicWin} from './logicWin';
import {v4 as uuidv4} from 'uuid'

const Field = ({playerOne, playerTwo}) =>{
    const [state, setState] =useState({
        arrSquares: Array(9).fill(null),
        count: 0
    });




    const winnerPlayer = () =>{
        const symbol = state.count % 2 === 0 ? 'X' : 'O';
        const result = logicWin.forEach((item, index) =>{
            const line = logicWin[index];
            if(state.arrSquares[line[0]] === symbol
                && state.arrSquares[line[1]] === symbol
                && state.arrSquares[line[2]] === symbol){
                alert(symbol + 'winn')
                setState({arrSquares: Array(9).fill(null), count: 0})
                // return symbol === 'X' ? console.log(line) : console.log(line)

            }
        })

    }

    const handelClick = (event) =>{
        let data = event.target.getAttribute('data');
        let current = state.arrSquares;
        if(current[data] === null){
            current[data] = state.count % 2 === 0 ? 'X' : 'O';
            setState({
                ...state,
                arrSquares: current
            })
            setState({
                ...state,
                count: state.count + 1
            })
        }
        winnerPlayer()

    }
    const showSquare = state.arrSquares.map((item, index) =>
        <Square key={uuidv4()} content={item} onClick={handelClick} data={index} />)

    return(
        <div className='field-container'>
            {showSquare}
            {/*<div className=''></div>*/}
        </div>
    )

}

export default Field;
