import React, {useState, useContext, useEffect} from 'react';

const ScoreContext = React.createContext();
export const useScore = () =>{
    return useContext(ScoreContext)
}

const GameContext = ({children}) =>{
    const [state, setState] = useState({
        playerOne: 0,
        playerTwo: 0,
        nameOne: '',
        nameTwo: '',
        line: null

    });
    useEffect(()=>{
        // const nameOne = prompt('Имя первого игрока ?')
        // const nameTwo = prompt('Имя второго игрока ?')
        // setState({
        //     ...state, nameOne: nameOne, nameTwo: nameTwo
        // })

    }, [])

    const increment = (activeWord) =>{
        if(activeWord === 'X'){
            setState({...state, playerOne: state.playerOne + 1})
        }
        if(activeWord === 'O'){
            setState({...state, playerTwo: state.playerTwo + 1})
        }
    };
    const showLine = (line) =>{
        const res = line.reduce((cart, item, index, arr)=>{
            return cart += item
        }, 0);
        if(res === 3){
            setState({...state, line: state.line = 'activeXOne'});
        }
    }
    console.log('this', state.line)


    return(
        <ScoreContext.Provider value={{
            visible: state,
            increment,
            playerName: state.name,
            showLine,

        }} >
            {children}
        </ScoreContext.Provider>
    )

}
export default GameContext;
